from django import formsfrom django.forms import ModelFormimport django_bootstrap3_formfrom url_generator.models import UrlGeneratorTask, LoadFile, LoadFileDatafrom django_file_form.forms import FileFormMixin, UploadedFileField, MultipleUploadedFileFieldfrom django.core.files.storage import FileSystemStoragefrom django.conf import settingsfrom django.core.files.storage import default_storageclass UrlGeneratorTaskModelForm(ModelForm):	class Meta:		model = UrlGeneratorTask		exclude = ('date', 'links_number', 'results')class BaseForm(FileFormMixin, django_bootstrap3_form.BootstrapForm):    title = django_bootstrap3_form.CharField()class FileForm(BaseForm):    file = MultipleUploadedFileField()    def save(self):        lf = LoadFile.objects.create(            title=self.cleaned_data['title']        )        for f in self.cleaned_data['file']:            LoadFileData.objects.create(                load_file=lf,                input_file=f            )            #up = default_storage.save('example/'+ f.name, f)        self.delete_temporary_files()class ExistingFileForm(FileForm):    def get_upload_url(self):        return reverse('example_handle_upload')